float sdRect(vec2 uv, vec2 upleft, vec2 size) {
    vec2 downright = upleft + size;
    float d = upleft.x - uv.x;
    d = max(uv.x - downright.x, d);
    d = max(upleft.y - uv.y, d);
    return max(uv.y - downright.y, d);
}


#define TIMESTEP 5.0

float f[8] = float[8](1.7, 1.4, 1.9, 1.5, 1.3, 1.1, 1.4, 1.9);
float data(float x) {
    float a = f[int(x * 5.1) % 8];
    float b = f[int(x * 4.2) % 8];
    float y = mix(a, b, sin(x * 0.1));
    return 5. + y * 5.;
}

float mx_data(float x) {
    float a = f[int(x * 5.1) % 8];
    float b = f[int(x * 4.2) % 8];
    return max(5. + mix(a, b, 1.) * 5., 5. + mix(a, b, -1.) * 5.);
}

float mn_data(float x) {
    float a = f[int(x * 5.1) % 8];
    float b = f[int(x * 4.2) % 8];
    return min(5. + mix(a, b, 1.) * 5., 5. + mix(a, b, -1.) * 5.);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 20.;

    vec3 col = vec3(0.);
    for (float i = 0.; i < 20.; i += 1.) {
        float x0 = floor(i + iTime / TIMESTEP);
        float x1 = x0 + 1.;
        if (i == 19.)
            x1 = i + iTime / TIMESTEP;
        float mx = mx_data(x1);
        float mn = mn_data(x1);
        float y0 = data(x0);
        float y1 = data(x1);
        float h = y1 - y0;
        vec2 sz = vec2(.25, h);
        if (h < 0.) {
          sz.y = abs(sz.y);
          y0 = y1;
        }
        float d1 = sdRect(uv, vec2(i, y0), sz);
        float d0 = sdRect(uv, vec2(i + 0.25 / 2., mn), vec2(.001, mx - mn));
        float d = min(d1, d0);
        float r = max(50. * d, 1.0);
        vec3 c = vec3(1./r, 0.2/r, 0.3/r);
        if (h < 0.)
            c = c.yxz;
        col += c;
    }

    col = col.yxz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
