void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 rectSize = iResolution.xy / 10.0;
    vec2 rectCoord = floor(fragCoord / rectSize);

    float randomValue = fract(sin(dot(rectCoord, vec2(11.5621, 78.943))) * (iTime/10000.) * 4358.5453);

    fragColor = randomValue < 0.3 ? vec4(1.) : vec4(0.);
}
