void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Set the background color to black
    vec4 backgroundColor = vec4(0.0, 0.0, 0.0, 1.0);

    // Set the red line color
    vec4 lineColor = vec4(1.0, 0.0, 0.0, 1.0);

    // Define the thickness of the line
    float lineWidth = 5.0;

    // Calculate the position of the line based on time
    float time = iTime;
    float lineX = 0.5 * iResolution.x + 0.5 * iResolution.x * sin(time);

    // Get audio data from iChannel0
    vec2 audioData = texture(iChannel0, fragCoord / iResolution.xy).rg;

    // Modulate the line color by the audio data
    lineColor *= vec4(audioData, 1.0, 1.0);

    // Check if the current fragment is part of the line
    if (abs(fragCoord.x - lineX) < lineWidth * 0.5)
    {
        fragColor = lineColor;
    }
    else
    {
        fragColor = backgroundColor;
    }
}
