vec3 pallete(float t){
    vec3 a = vec3(0.500,0.500,0.358);
    vec3 b = vec3(0.375,0.375,0.588);
    vec3 c = vec3(1.000,1.000,-0.882);
    vec3 d = vec3(0.000,-2.712,-1.242);

    return a + b*cos( 5.28318*(c*t+d) );

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;
    vec2 uv0=uv;
    vec3 finalColor = vec3(0.0);

    for(float i=0.0;i<2.0;i++){

    uv=fract(uv*2.0)-.5;

    float d = length(uv);

    vec3 col = pallete(length(uv0)+iTime);

    d = cos(d * 5.5 + iTime*1.3) / 7.;
    d = abs(d);
    d = 0.02/ d;

    finalColor+=col * d;
    }
    fragColor = vec4(finalColor,1.0);

}
