float squared(float value) { return value * value; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uvTrue = fragCoord.xy / iResolution.xy;
    vec2 uv = -1.0 + 2.0 * uvTrue;

	float lineIntensity;
    float glowWidth;
    vec3 color = vec3(0.0);

	 float Y = uv.y;
        lineIntensity = 0.4 + squared(1.6 * abs(mod(uvTrue.x  + iTime,2.0) - 1.0));
		glowWidth = abs(lineIntensity / (150.0 * Y));
		color += vec3(glowWidth,
                      glowWidth,
                      glowWidth);

	fragColor = vec4(color, 1.0);
}
