void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy / 0.064;//0.042 alt zoom
    uv.y += 1.8e6;//uv.y += 2.35e6; //alt lower res

    float pi = 3.14159;

    for (float i=1.; i<2.; ++i){
    pi  *= uv.y * pi * 0.035;
    pi  += uv.x - uv.y / i + i * pi;
    uv.y+=sin(pi / 12.0 + uv.y);
    uv.y+=cos(1.525 * pi);
    }

    // Time varying pixel color
    vec3 col = 0.5 + 0.8 * sin(iTime * 2.0+
    uv.yyy+vec3(abs(pi+uv.y)*1.3,
    abs(pi+uv.y+0.2)*1.34,
    abs(pi+uv.y+0.1)*1.34));

    // Output to screen
    fragColor = vec4(col,1.0);
}
