void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / max(2.1*iResolution.x, 0.0);
    float t = iTime;
    float time = t * 4.0;
    float a = atan(p.y, p.x);
    float r = length(p);

    r += sin(r * 5.0 + time) * 0.06; //bounce scale

    vec2 col = atan(0.6*vec2(0.1 + 6.0 * tan(1.0+r * 24.0 - time), r));
    col += 1.5/1.5*vec2(0.6 * atan(t - time));
    col = vec2(col.x+col.y/0.25*r*r*r*r*r*r*r);
    col = vec2(col.y*col.x);
    col -= 1.5-r;
    fragColor = vec4(col.x,col, 1);
}
