void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 n, q, p = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    float d = dot(p, p), S = 9.0, a = 0.0, j = 0.0;

    for (float i = 0.0; i < 30.0; i++) {
        mat2 m = mat2(cos(5.0), -sin(5.0), sin(5.0), cos(5.0));
        p *= m; n *= m;
        q = p*S + iTime*4.0 + sin(iTime*4.0 - d*6.0)*0.8 + j + n;
        a += dot(cos(q)/S, vec2(0.2));
        n -= sin(q);
        S *= 1.2;
        j += 1.0;
    }
    fragColor = (a + 0.2)*vec4(4.0, 2.0, 1.0, 0.0) + a + a - d;
}
