void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 draw = texture(iChannel0,uv);
    fragColor = vec4(draw.rgb,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    float radius = .07;
    vec2 center = vec2(.5 + cos((1.57+iTime)*2.)*.25,.5 + sin((1.57+iTime)*3.)*.25);
    radius *=radius;
    vec3 col = vec3(uv.xy,.5);
    vec4 buff = texture(iChannel0, uv);
    vec4 outp = vec4(col,1.);
    outp += buff*70.;
    outp /= 71.;
    if (-sqrt(radius - pow((uv.x - center.x)*aspect,2.)) + center.y < uv.y && sqrt(radius - pow((uv.x - center.x)*aspect,2.)) + center.y > uv.y) {
        col = vec3(uv.y,.85,uv.x);
        outp = vec4(col,1.);
    }
    // Output to screen
    fragColor = outp;
}
