void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy / iResolution.xy;

    float frequency = 10.;

    // Thickness should go from 1. to the frequency.
    float thickness = 1.4;

    // Antialias must be a number between 0. and 0.99
    float antialias = 0.5;

    // See https://graphtoy.com/?f1(x,t)=abs(x)&v1=true&f2(x,t)=smoothstep(0.3,%200.0,%20f1(x,t))&v2=true&f3(x,t)=&v3=false&f4(x,t)=&v4=false&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=1&coords=0,0,1.3333333333333333
    // See https://graphtoy.com/?f1(x,t)=1-fract(x)&v1=true&f2(x,t)=fract(x)&v2=true&f3(x,t)=step(fract(x*0.5),0.5)&v3=true&f4(x,t)=step(fract((x+1)*0.5),0.5)&v4=true&f5(x,t)=(1-fract(x))*step(fract(x*0.5),0.5)+fract(x)*step(fract((x+1)*0.5),0.5)&v5=true&f6(x,t)=&v6=false&grid=1&coords=0,-0.7054654766281401,3.8041556081466794
    // See https://graphtoy.com/?f1(x,t)=(1-fract(x))*step(fract(x*0.5),0.5)+fract(x)*step(fract((x+1)*0.5),0.5)&v1=false&f2(x,t)=smoothstep(0.2,%200,f1(x))&v2=false&f3(x,t)=x*10&v3=false&f4(x,t)=(1-fract(f3(x)))*step(fract(f3(x)*0.5),0.5)+fract(f3(x))*step(fract((f3(x)+1)*0.5),0.5)&v4=false&f5(x,t)=smoothstep(0.6,%200,f4(x))&v5=true&f6(x,t)=&v6=false&grid=1&coords=0,0,1.3333333333333333

    float coord = uv.y * frequency;

    float gradient1 = 1. - fract(coord);
    float zigzag1 = (gradient1) * step(fract(coord * 0.5), 0.5);

    float gradient2 = fract(coord);
    float zigzag2 = fract(coord) * step(fract((coord + 1.) * 0.5), 0.5);

    float zigzag = zigzag2 + zigzag1;

    vec3 col = vec3(smoothstep(1. - antialias, 1., zigzag * thickness));


    fragColor = vec4(col, 1.0);
}
