#define TAU 6.28318530
#define PLASTIC 1.3247179572

void mainImage(out vec4 O, in vec2 U) {
    vec2 uv = ( 2.*U - iResolution.xy ) / iResolution.y;
    uv *= abs(2.0*sin(iTime*0.5));

    vec2 tmp = uv;
    if (U.y > iResolution.y / 2.){
        uv.x = -uv.y;
        uv.y = tmp.x;
    } else {
        uv.y = -uv.x;
        uv.x = tmp.y;
    }

    O = vec4(vec3(
        smoothstep(abs(sin(iTime*.8)), 2.*abs(cos(iTime*.9)),
            abs(length(uv) - fract(atan(uv.y, uv.x) * PLASTIC / TAU)))
        ), 1.);
}
