// 2023-10-1  It's been exactly a year now
// 原：眩晕 视觉误差3
// 简单的视觉误差2
// 图片非原创

// 图片会动的眩晕感觉，即使暂停


#define PI 3.1415926535

void mainImage(out vec4 O, vec2 u)

{

        vec2 R = iResolution.xy, v;

        u = 88.* (u + u - R)/ R.y;


        //u /= dot(u,u);



        float a = atan(u.y,u.x), r = length(u), f = 38., g;

        vec3 x;



        g = 2. * PI / f; 

        x = 2. * PI * fract( a / g + 2. * vec3(0,1,2)/3.) ;

        x = sin(x)+1.;

        x.x = pow(x.x,.01);x.y = pow(x.y,.012);x.z = pow(x.z,.013);

        x = log(r)/log(1. + x) *22.;



        O.xyz =  sin(x*vec3(1.0,1.0,1.0)+01.*vec3(1,2,7))*.4+.5;
        //O.z=O.z*1.3-.3*O.x*O.y;

}
