void mainImage(out vec4 O, vec2 I)
{
    vec2 R = iResolution.xy, p = 1.2 * (I + I - R) / R.y;

    #define l(d) pow(max(dot(vec3(p, sqrt(max(1. - dot(p, p), 0.))), d), 0.), 2.)
    O = (abs(p.x) < 1.6 ? 1.4 : 0.) * sin(atan(sqrt(vec4(2, .7, .2, 0) * l(vec3( 2, 1,  3))
                                                  + vec4(1,  4,  4, 0) * l(vec3(-1, 2, -2))) / (1. + dot(p, p) * .36)) / 2.);
}
