vec3 palette (float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.388, 0.428, 0.5);
    vec3 c = vec3(-0.032, 0.368, 1.0);
    vec3 d = vec3(0.0, 0.333, 0.667);

    return a + b*cos(6.28318*(c*t*d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 4.0 - iResolution.xy) / iResolution.xy;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);


    for (float i = 0.0; i <2.0; i++){
        uv = fract(uv * 0.5) -0.5;
        float d = length(uv);

        vec3 col = palette(length(uv0*1.5) + iTime*.2);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);
        d = 0.015/d;
        col *= d;

        finalColor += col * d;
    }
    for (float i = 0.0; i <2.0; i++){
        uv = fract(uv * 0.5) -0.5;
        float d = length(uv);

        vec3 col = palette(length(uv0*1.2) + iTime*.8);

        d = sin(d*6. + iTime)/6.;
        d = abs(d);
        d = 0.010/d;
        col *= d;

        finalColor += col * d;
    }
    vec4 tex = texture(iChannel0, vec2(4.0 - uv.x, uv.y));
    vec4 fc = vec4(finalColor, 1.0);

    //fragColor = vec4(finalColor, 1.0);
    fragColor = fc*(tex*4.0);
}
