void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float ss = 1.5/iResolution.x;  // sharp but smooth edges for every screenres

    vec3 bg = vec3(0.89,0.32,0.43);
    vec3 c1 = vec3(0.97,0.95,0.84);
    vec3 c2 = vec3(0.32,0.71,0.66);
    vec3 c3 = vec3(0.98,0.87,0.55);

    vec3 col = uv.x<.0 ? uv.y<.0 ? c1 : c2 : uv.y<.0 ? bg : c3;
    col = mix(col, bg, smoothstep(-ss,ss, length(uv)-.35));

    fragColor = vec4(col,1.0);
}
