float fraq( float v ){
 return v-floor(v);
}

float myfn( float v ){
 return fraq(fraq(fraq( v * fraq( v * 0.8723898723)) * 3.7928749238 )*177.14323);
}


void mainImage( out vec4 fragColor, in vec2 c )
{

    float w = iResolution.x;
    float h = iResolution.y;
    float wh = w*h;
    float offset = w*h*iTime*.125;
    float t = 1.25+iTime*16.;
    float t2= iTime*16.;
    float b = c.x*0.01 + c.y*w*0.01;
    float v1 = myfn( b + floor(t) );
    float v2 = myfn( b + floor(t)+1.0 );
    float v3 = myfn( 2852.34234 + b + floor(t2)     +offset);
    float v4 = myfn( 2852.34234 + b + floor(t2)+1.0 +offset);
    float v = 0.5*((v1+(v2-v1)*fraq(t))+(v3+(v4-v3)*fraq(t2))); 

    vec3 col = vec3( v, v, v );

    // Output to screen
    fragColor = vec4(col,1.0);
}
