float zT(vec2 uv, vec3 v1, vec3 v2, vec3 v3){ 
    float Z = v1.z + (v2.z - v1.z) * uv.x + (v3.z - v1.z) * uv.y;
    return Z;}
float zC(vec2 Ctr, float r, float offset){
    float Z =offset+sqrt((r*r-(Ctr.x)*(Ctr.x)-(Ctr.y)*(Ctr.y)));
    return Z;
}
bool insT(vec3 v1, vec3 v2, vec3 v3, vec2 uv){

    float ar = 0.5 * (-v2.y * v3.x + v1.y * (-v2.x + v3.x) + v1.x * (v2.y - v3.y) + v2.x * v3.y);    float x1 = 1.0 / (2.0 * ar) * (v1.y * v3.x - v1.x * v3.y + (v3.y - v1.y) * uv.x + (v1.x - v3.x) * uv.y);
    float x2 = 1.0 / (2.0 * ar) * (v1.x * v2.y - v1.y * v2.x + (v1.y - v2.y) * uv.x + (v2.x - v1.x) * uv.y);    float x3 = 1.0 - x1 - x2;
    return (x1 >= 0.0 && x2 >= 0.0 && x3 >= 0.0);
    }

bool insC(vec2 uv, vec2 Ctr, float r) {
    float d = length(vec2(Ctr.x, Ctr.y)) - r;
    if(d>0.) return false; else return true;
  }
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord/iResolution.xy * vec2(iResolution.x/iResolution.y, 1.0);

    vec3 v_t11 = vec3(0.1, 0.1, 1.3);
    vec3 v_t12 = vec3(0.7, 0.1, 1.3);
    vec3 v_t13 = vec3(0.1, 0.7, 1.3);    

    vec3 v_t21 = vec3(0.5+sin(6.*iTime)/3.0, 0.4+sin(6.*iTime)/3.0, -2.0);
    vec3 v_t22 = vec3(0.9+sin(6.*iTime)/3.0, 0.2+sin(6.*iTime)/3.0, 3.0);
    vec3 v_t23 = vec3(0.9+sin(6.*iTime)/3.0, 0.8+sin(6.*iTime)/3.0, 3.0);   

    vec3 v_t31 = vec3(0.3+cos(iTime)/3.0, 1.0-cos(iTime)/3.0, 2.); 
    vec3 v_t32 = vec3(0.3+cos(iTime)/3.0, 0.5-cos(iTime)/3.0, 3.);
    vec3 v_t33 = vec3(0.7+cos(iTime)/3.0, 0.1-cos(iTime)/3.0, 3.);    

    vec2 offset = vec2(sin(iTime*2.)*0.2, 7.0*cos(iTime*2.)*0.2);  

    vec2 Ctr1=vec2(0.0, 0.0); 
    Ctr1.x= uv.x+offset.x-0.3;
    Ctr1.y= uv.y+offset.x-0.3;
    float r_1 = 0.4;

    vec2 Ctr2=vec2(0.0, 0.0);
    Ctr2.x= uv.x+offset.x-1.0;
    Ctr2.y= uv.y+offset.y-0.5;
    float r_2 = 0.3; 

    bool arr[5];
    arr[0]=insT(v_t11, v_t12, v_t13, uv);
    arr[1]=insT(v_t31, v_t32, v_t33, uv);
    arr[2]=insT(v_t21, v_t22, v_t23, uv);
    arr[3]=insC(uv, Ctr2, r_2);
    arr[4]=insC(uv, Ctr1, r_1);
        float z[5];
    z[0] = zT(uv, v_t11, v_t12, v_t13);
    z[1]= zT(uv, v_t31, v_t32, v_t33);
    z[2] = zT(uv, v_t21, v_t22, v_t23); 
    z[3] =  zC(Ctr2, r_2 , 7.0);
    z[4]=zC(Ctr1, r_1 , 1.15); 


    vec3 stripes01 = vec3(0.0, 0.0, 0.0);
    vec3 stripes02 = vec3(0.3, 0.0, 0.0);
    vec3 stripes11 = vec3(0.5, 0.3, 0.0);
    vec3 stripes12 = vec3(0.3, 0.2, 0.0);
    vec3 stripes1 = mix(stripes01, stripes02, mod(floor(uv.x* 15.0), 2.0)); 
    vec3 stripes2 = mix(stripes11, stripes12, mod(floor((uv.y+offset.y)* 15.0), 2.0));

    vec4 col[5];
    col[0]=vec4(stripes1, 1.0);
    col[1]=vec4(0.3, 0.3, 0.3, 1.0);
    col[2]=vec4(0.2, 0.1, 0.3, 1.0);
    col[3]=vec4(stripes2, 1.0);
    col[4] = vec4(0.4, 0.3, 0.4, 1.0);
    vec4 bg=vec4(0.3, 0.0, 0.0, 1.0);
    float max=-100.0;

        for(int i=0; i<5; i++){

        if(arr[i]){if(z[i]>max){bg=col[i]; max=z[i];}} 
        }
        fragColor = vec4(bg);
    return;}
