float tau = atan(1.)*4.*2.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Coordinates
    float resmax = max(iResolution.x, iResolution.y);
    vec2 st = fragCoord.xy/resmax;
    vec2 center = vec2(0.5*iResolution/resmax);

    // Discretization of canvas
    vec2 stmoving = vec2(st)+vec2(sin(iTime/100.),cos(iTime/100.));
    int x = int(stmoving.x*resmax+resmax*2.);
    int y = int(stmoving.y*resmax);

    // Pulse effect
    float dist_center = length(st-center);

    float cycle = sin(mod(iTime/3.0-dist_center/2.,1.)*tau)*2.0;
    cycle = max(cycle,0.);
    cycle = 1.-cycle;
    cycle = pow(min(cos(tau/4.0*cycle),1.0 - abs(cycle)),3.0);
    float bright = 0.4+cycle*0.4;

    // Circuits
    float modul = 13.0+sin((iTime/10.0+st.y-st.x*sin(iTime/10.0)*0.4)/100.0)*5.0;

    float alien = step(mod(float(x^y), modul), 1.);
    alien += 0.2*step(mod(float(x^y), modul+2.), 1.);

    // Colors
    vec3 color = vec3(0.2,0,0.2);
    color += vec3(bright*alien, cycle*0.4*alien, bright*alien);
    fragColor = vec4(color, 1);
}
