void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 temp1 = texture(iChannel0,uv);
    vec4 temp2 = texture(iChannel1,uv);

    if(uv.x>0.5){
        if(uv.y>0.5){
            fragColor = temp1;
        }else{
            fragColor = temp2;            
        }
    }else{
        if(uv.y>0.5){
            fragColor = temp2;
        }else{
            fragColor = temp1;
        }
    }


}
