#ifdef GL_ES
precision mediump float;
#endif

const float M = 10.;
const float A = 4.;

vec2 F(vec2 p, vec2 q0) {
    return vec2(p.x*p.x-p.y*p.y+q0.x, 2.*p.x*p.y+q0.y);
}

float sqnorm(vec2 p) {
    return p.x*p.x+p.y*p.y;
}

float iterate(vec2 p0, vec2 q0, float N) {
    vec2 p = vec2(p0.y,p0.x);
    for (float i = 0.; i < N; i++) {
        p = F(p,q0);
    }
    float sqn = sqnorm(p);
    return sqn > 1. ? 1. : sqn;
}

vec3 orange = vec3(0.875,0.401,0.142);

const float sm = 0.003;
const vec2 r_size = vec2(.04,.54);


float rect(vec2 center, vec2 size, vec2 pos) {
    return smoothstep(center.x-size.x-sm,center.x-size.x+sm,pos.x) *
           smoothstep(center.y-size.y-sm,center.y-size.y+sm,pos.y) *
           smoothstep(-center.x-size.x-sm,-center.x-size.x+sm,-pos.x) *
           smoothstep(-center.y-size.y-sm,-center.y-size.y+sm,-pos.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float N = iTime;

    vec2 q0 = vec2(.4, + .6*sin(iTime/2.));

    vec2 st_0 = 2.*(fragCoord.xy - .5*iResolution.xy) / min(iResolution.x,iResolution.y);
	vec2 st = A*(fragCoord.xy - .5*iResolution.xy)/min(iResolution.x,iResolution.y);
	float c = 1. - iterate(st,q0,iTime/5.);

    float rects = rect(vec2(-1.3,0.),r_size,st_0) + rect(vec2(1.3,0.),r_size,st_0);
    fragColor = vec4(mix(orange, vec3(0.), max(rects, c)), 1.);

}
