#define R iResolution.xy

float Cir (vec2 uv, float r, bool blur) {
    float a = blur ? 0.01-sin(iTime*2./2.0) *0.1: 0.;
    float b = blur ? 0.13: 5./R.y;
    return smoothstep(a, b, length(uv)-r);
}

float Cir2 (vec2 uv, float r) {
    float a = 0.0;
    float b = 0.1;
    return smoothstep(a, b, length(uv)-r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = 0.7;
    vec2 uv = (fragCoord-.5*R.xy)/R.y;
    vec2 t = vec2(sin(iTime*2./2.0+m), cos(iTime*3./2.0+cos(iTime*.5/2.0+m)))*.1;
    vec2 t2 = vec2(sin(iTime*2./2.0+m)+0.2, cos(iTime*3./2.0+cos(iTime*.5/2.0+m))-0.2)*.1;

    vec3 Col0 = vec3(1.2);
    vec3 Col1 = vec3(0.2+uv.y*2., .4+uv.x*-0.2, 1.4)*1.2;
    vec3 Col2 = vec3(0.92);
    vec3 Col3 = vec3(0.12);

    float cir1 = Cir(uv-t,0.15+sin(iTime*2./2.0)*0.05, false);
    float cir11 = Cir(uv-t,0.15+sin(iTime*2./2.0)*0.1, false);
    float cir2 = Cir(uv+t, 0.2, false);
    float cir2B = Cir(uv+t, 0.15, true);
    float cir3 = Cir2(uv-t2, 0.05+sin(iTime*2./2.0)*0.1);

    vec3 col = mix(Col1+vec3(0.3,0.1,0.), Col2, cir2B);
    col = mix(col, Col0, cir1);
    col = mix(col, Col1, clamp(cir1-cir2, 0., 1.));
    col = mix(col, Col3, clamp(cir1-cir3, 0., 0.1));
    fragColor = vec4(col,1.0);

}
