void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 tap = texture(iChannel0, uv).rgb;
    vec3 col = tap;

    int maxMip = int(log2(max(iResolution.x, iResolution.y)) * 0.64);

    for (int i = 1; i <= maxMip; i++)
    {
        vec3  mipTap   = texture(iChannel0, uv, float(i)).rgb;
        float mipDelta = distance(tap, mipTap);
        float mipW     = exp(-mipDelta * mipDelta * 200.0);

        if (uv.x < 0.5) col = mix(col, mipTap, mipW);
    }

    fragColor = vec4(col,1.0);
}
