const vec2 TexSize = vec2(256.0,32.0);
const vec2 Offset = vec2(2.0,5.0);
const vec2 FrameSize = vec2(38.0,25.0);
const float Space = 2.0;
const float CountFrames = 6.0;
const float Speed = 6.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float frameIndex = floor(mod(iTime * Speed,CountFrames));

    uv += Offset / TexSize;
    uv *= FrameSize / TexSize;
    uv.x += ((FrameSize.x + Space) * frameIndex ) / TexSize.x;

    fragColor = texture(iChannel0,uv);
}
