void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 source =  vec2(iMouse.xy/iResolution.xy)- uv;
    float disToSource = cos(length(source));

    float _sin = sin(disToSource * 80.0 +iTime*2.0) ;
    float _discard = disToSource;

    vec2 offset = source* _sin * _discard;
    uv += offset;
    fragColor= texture(iChannel0, uv); 
}
