float sampleSize = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord / iResolution.xy;

    vec2 tileVariety = step(0.5, mod((sampleSize * uv), 1.0));
    float tileType = (1.0-tileVariety.x) * (1.0 - tileVariety.y) + tileVariety.x * tileVariety.y;

    float stableRegion1 = 0.5 - abs(iMouse.x / iResolution.x - 0.5);


    tileType = 1.0 - (1.0 - tileType) * (step(stableRegion1, uv.x));


    float stableRegion2 =  0.5 + abs(iMouse.x / iResolution.x - 0.5);

    tileType *= 1.0 - (step(stableRegion2, uv.x));


    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, mod(uv + vec2(0.1 * iTime,0.0), 1.0));
    vec4 col = tileType * col1 + (1.0 - tileType) * col2;


    fragColor = col;
}
