vec3 palette( in float t )
{
    vec3 a = vec3(0.138, 0.532, 0.132);
    vec3 b = vec3(0.820, 0.667, 0.074);
    vec3 c = vec3(0.227, 0.317, 1.398);
    vec3 d = vec3(3.934, 6.146, 6.064);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 0.5 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColour = vec3(0.0);

    for (float i = 0.00; i < 3.0; i++) {
        uv = fract(uv * 0.5) - 10.0;

        float d = length(uv);

        vec3 col = palette((length(uv0) + iTime * 1.5) + sin(iTime * 0.5));

        d = sin(d * 12.0 + iTime) / 6.0;
        d = abs(d);
        d = 0.0065 / d;

        finalColour += col * d;
    }

    fragColour = vec4(finalColour, 1.0);


}
