float hash(float num1, float num2) {
    return abs(fract(72.53 * sin((num1/9711.0 + 7.0) * 315.0 + 127.0) * cos((num2/3917.0+11.0) * 513.0) * 7759.0));
}

const int HW = 5;
const int DW = 21;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = fragCoord.y;

    float hs[HW*2];
    for(int i=0;i<HW;++i)hs[i]=hash(x+float(i-HW/2),floor(iTime*10.0)+y);
    int idx = 0;
    for(int j=0;j<DW;++j) {
        for(int i=0;i<HW;++i) {
            float c = 2.*hs[idx*HW+i];
            c += hs[idx*HW+((i-1+HW)%HW)];
            c += hs[idx*HW+((i+1)%HW)];
            c += hash(x+float(i-HW/2), floor(iTime*10.0)+float(j)+y)*1.5;
            c /= 5.5;
            hs[(1-idx)*HW+i]=clamp(c,0.,1.);
        }
        idx = 1-idx;
    }

    vec3 col=vec3(hs[idx*HW+HW/2],hs[idx*HW+HW/2-1],hs[idx*HW+HW/2+1]);
    fragColor = vec4(col,1.0);
}
