// just fiddled with uv's a bit
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y += 0.25;
    vec2 uv2 = uv - vec2(0, iTime)/1.;
    vec2 uv3 = uv - vec2(0, iTime)/4.5;
    vec2 uvNoise1 = uv/5. - vec2(0.1, iTime)/5.;
    vec2 uvNoise2 = uv/vec2(1.,3.) - vec2(0.1, iTime)/2.;

    // Time varying pixel color
    vec3 col = vec3(0);

    vec2 M = iMouse.xy/iResolution.xy;
    float offset = sin(iTime)*.1*(uv.y*1.5)-M.x*.1*(uv.y*1.5);

    float noise1 = texture(iChannel0, uvNoise1).r;
    float noise2 = texture(iChannel0, uvNoise2).r;
    uv2 = -0.5+uv2+(noise1*.05+noise2*.051)+offset;

    uv2.x *= uv.y*5.;
    float tex = texture(iChannel0, uv2).r*.01*(1.-uv.y);
    tex = textureLod(iChannel0, uv2+vec2(tex,tex)*.6, mix(5.,4.5, uv.y/1.*(1.0-abs(uv.x*3.-1.5)))).r;

    uv2 = abs(uv*vec2(1.,0.8)-vec2(.5,0.))*2.;

    uv2.y -= uv.y/1.*noise1+uv.y/1.*noise2*.3;
    uv2.y *= 1.3;
    uv2.x += noise1*.25;
    uv2 = smoothstep(0., 1.2, uv2);
    //uv2.x += sin(iTime)*.1*(uv.y*1.5);
    float d = 1.-dot(uv2,uv2);
    tex *= d;
    //col = vec3(tex);

    // coloring
    // smokey
    vec3 col1 = mix(vec3(0.094,0.204,0.204),vec3(1.)*1.5,tex);

    //col = mix(vec3(0.094,0.204,0.204),vec3(1.)*1.5,tex);
    //col = mix(vec3(0.094,0.204,0.204)*2.,vec3(1.)*1.8,tex)-.4;
    col = 0.7*mix(vec3(0.220,0.204,0.102)*2.,vec3(1.)*1.8,tex)-.4;
    col += 1.0*mix(vec3(0.300,0.204,0.102)*2.5,vec3(1.)*1.8,tex)-.5;
    //col *= vec3(0.094,0.204,0.204)*5.;
    col = clamp(col, 0., 1.);
    col = col*col;

    // green to purple 
    //vec3 col2 = mix(vec3(0), vec3(0.055,0.145,0.145)*11.5,tex);
    //col2 *= mix(vec3(1.000,1.000,1.000),vec3(0.984,0.039,1.000)*2.,-.2+uv2.y/1.9*(clamp((1.-d)*5., 0., 1.)))*1.;
    //col2 += tex*tex*tex;

    col = mix(col, col1, cos(3.14-iTime/5.)*0.5+0.5);

    //fragColor = vec4(vec3(tex),1.0);
    fragColor = vec4(col,1.0);
    return;
}
