void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord/iResolution.yy) - vec2(16./9.,1.)/4.;

    vec3 color = vec3(0.0);
    float h = texture(iChannel0, vec2(20.)).x;
    float h2 = texture(iChannel0, vec2(150.)).x;

    vec2 pos = vec2(0.5)-st;
    pos.y -= 0.25;

    float r = length(pos)*2.0+h;
    float a = atan(pos.y,pos.x);

    float f = cos(a*4.) * cos(a*h2*250.);

    color = 1.-vec3(step(f, r));

    fragColor = vec4(color,1.0);
}
