float scale = 5.0;
const float pi = 3.14159;

float rand(float s){ return fract(sin(s*23.3249)); }
vec2 pos(float t){ return vec2(sin(t*9.), cos(t*pi*2.)) * 0.5 + 0.5; }

vec4 voronoi(vec2 uv)
{
    float o = 1e2;
    float id = 0.;
    vec2 v;

    for (int x = -1; x<=1; x++)
    {
        for (int y = -1; y<=1; y++)
        {
            vec2 cell = vec2(floor(uv.x), floor(uv.y));
            vec2 t = uv - (
                    cell + vec2(x,y) + pos(
                        rand(
                            cell.x + float(x) + (float(y) + cell.y)*pi
                        ) + iTime*0.2
                    )
                );
            float a = length(t);
            if (a < o){
                o = a; id = rand(cell.x + cell.y*pi + float(x) + float(y)*pi);
                v=t;
            }
        }
    }

    return vec4(o, id, -v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = scale * fragCoord/iResolution.y;

    vec4 v = voronoi(uv);
    float o = v.x;
    float id = v.y;

    float s = mod(iTime, 3.);
    fragColor = s < 1.?vec4(o * 0.8):s < 2.?v.zwxx:vec4(id);
}
