void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy;
    float W = iMouse.z>0. ? 8.*iMouse.y/R.y : log2(R.y/45.);
    O = fract(iTime/2.)<.5
          ?     pow( textureLod(iChannel1, u/R, W ), vec4(1./30.) )
          : 1.- pow( textureLod(iChannel2, u/R, W ), vec4(1./30.) );
    O = sqrt(O); // sRGB encode
}

// === source =======================

void mainImage( out vec4 O, vec2 u )
{
    O = texture(iChannel0, u/iResolution.xy);
    O *= O;  // sRGB decode
}

// === for max ======================

void mainImage( out vec4 O, vec2 u )
{
    O = pow( texture(iChannel0, u/iResolution.xy), vec4(30) );
}

// === for min ================

void mainImage( out vec4 O, vec2 u )
{
    O = pow( 1. - texture(iChannel0, u/iResolution.xy), vec4(30.) );
}
