const float TIME_SPEED = 0.25;
const float UPSCALE = 2.0;

float dither(float value, float mask, float shades)
{
    value *= (shades - 1.0);

    float base = floor(value);
    float fr = value - base;

    return (base + (fr >= mask ? 1.0 : 0.0)) / (shades - 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float ditherMask = texture(iChannel0, fragCoord.xy / UPSCALE / vec2(textureSize(iChannel0, 0).xy)).r;

    // I like to move it, move it
    ditherMask = fract(ditherMask + iTime * TIME_SPEED);

    float value = uv.y;

    if (uv.x < 0.25)
        value = dither(value, ditherMask, 2.0);
    else if (uv.x < 0.5)
        value = dither(value, ditherMask, 4.0);
    else if (uv.x < 0.75)
        value = dither(value, ditherMask, 8.0);

    vec3 color = mix(vec3(1.0,0.3,0.0), vec3(0.0,0.3,1.0), value);
    color = pow(color, vec3(1.0 / 2.2));

    if ((int(fragCoord.x / UPSCALE) % (int(iResolution.x / UPSCALE) / 4)) == 0)
        color = vec3(0.0);

    fragColor = vec4(color, 1.0);
}
