void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 col = texture(iChannel0, uv);

    // Output to screen
    fragColor = col;
}

int neighbours(ivec2 p){
    int num=0;

    for(int i=-1; i<=1; i++) {
        for(int j=-1; j<=1; j++) {
            if( i==0 && j==0) continue;
            num += texelFetch(iChannel1,p+ivec2(i,j),0).r > .5 ? 1:0;
        }
    }
    return num;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = vec4(0);

    if(iFrame<10)
        col = texture(iChannel0,uv);
    else{ 
        bool alive = texelFetch(iChannel1,ivec2(fragCoord),0).r > .5;
        int num = neighbours(ivec2(fragCoord));

        int next = 0;
        if(alive && (num ==2 || num==3))next = 1;
        else if(!alive && num == 3) next = 1;
        else next = 0;

        col = vec4(next);
    }
    fragColor = col;
}
