#define PI acos(-1.)

mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.0);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=4.;
    p*=rotate3D(iTime*.3,vec3(-1));

    for(j=0.;j++<8.;)
        p=abs(abs(p)*rotate3D(PI,vec3(-1))-.005)-.05,
        d=max(max(p.z,p.y),p.x),
        d-=dot(p,p)/2e4;
    e+=d;
}o.rgb+=hsv(iTime*.4,.5,4.)/i;
}
