vec3 palette( float t )
{
    vec3 a = vec3(0.1, 0.2, 0.3);
    vec3 b = vec3(0.4, 0.5, 0.6);
    vec3 c = vec3(0.7, 0.8, 0.9);
    vec3 d = vec3(0.10, 0.11, 0.12);

    return a + b*cos( 6.28318*(c*t+d) );
}

