void mainImage( out vec4 O, vec2 U ){

if (iFrame < 20){

    O = vec4(0);
    if (U == vec2(.5)){
        O += 1.;
    }

} else {

    O = texelFetch(iChannel0, ivec2(U), 0);

    int sum = 0;
    for (int x = -1; x <= 1; x++){
        for (int y = -1; y <= 1; y++){
            sum += int(texelFetch(iChannel0, ivec2(U) + ivec2(x, y), 0).x);
        }
    }


    O = sum==1?vec4(1):O;

}}
