vec2 cpow(vec2 c, float exponent) {
    if (abs(c.x) < 1e-5 && abs(c.y) < 1e-5) {
        return vec2(0,0);
    }
    else {
        float cAbs = length(c);
        vec2  cLog = vec2(log(cAbs), atan(c.y,c.x));
        vec2  cMul = exponent*cLog;
        float expReal = exp(cMul.x);
        return vec2(expReal*cos(cMul.y), expReal*sin(cMul.y));

    }

}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = 1.0;
    vec2 dimensions = vec2 (2.2,2.2);
    vec2 center = vec2(0.0,0.0);
    int iterations = 1000;
    float colors = 150.0;

    vec2 coord = fragCoord/iResolution.xy;
    coord = coord - vec2(0.5,0.5);
    coord = vec2 (coord.x * (iResolution.x / iResolution.y),coord.y);

    if (iMouse.z > 0.0) {
        zoom = 2.0;
        coord = coord + (iMouse.xy/iResolution.xy - vec2(0.5,0.5));    

    }



    coord = coord * dimensions / zoom;
    coord = coord + center;

    vec2 z = vec2(0.0,0.0);

    for(int iteration = 0; iteration < iterations; iteration++) {
        z = cpow(z,3.0 + sin(iTime / 3.0));
        z = z + coord;

        if (abs(z.x) > 2.0) {
            vec3 color = vec3(float(iteration)/colors,1.0,1.0);
            color = hsv2rgb(color);
            fragColor = vec4(color,1.0);
            return;

        }

    }

    fragColor = vec4 (0.0,0.0,0.0,0.0);

}
