void mainImage( out vec4 O, vec2 u )
{
    float t = iTime, s, x;
    vec2  R = iResolution.xy ,
          U = ( u -.5*R ) / R.y,
          D = vec2( sin(t+t)*.6, cos(t+t)*.24873); 

    s = u.y/u.x * cos(D.x+U.y)/2.,
    x = U.x - D.x;

    U = abs(   ( U-D - vec2( 0, cos(t)*.142 ) )
             * mat2( cos( t - atan( U.y/U.x +s-D.x ) - D.y* U.x/(x*x-s) +vec4(0,33,11,0) ) ));

   O = mix( vec4(1),
             cos( t * vec4(.156, .117, .388, 0) ),
             smoothstep( 1.5/R.y, 0., max( U.x, U.y ) - s )
           );
}
