    vec3 baseC = vec3(100., 158., 158.) / 255.;
vec3 mainC = vec3(255., 192., 183.) / 255.;

const float da = 60.;
const float r = 84.;


const float pi = 3.14;
const float a = da * pi / 180.;
const float h = r / tan(a / 2.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    bool mask = distance(fract(xy.x/r),.5) -
                distance(fract(xy.y/h),.5) > 0.;

    fragColor = mask ? vec4(mainC, 1.) : vec4(baseC, 1.);
}
