void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizar las coordenadas
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;

    // Generar fractal para el fondo
    vec2 c = vec2(0.5*sin(iTime*0.1), 0.5*cos(iTime*0.1));
    vec2 z = uv;
    vec3 col = vec3(0.0);
    for (int i = 0; i < 100; i++) {
        if(length(z) > 2.0) break;
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        col += vec3(0.02, 0.03, 0.01);
    }

    // Mover las coordenadas con el tiempo
    float t = mod(iTime, 4.0) - 2.0;
    uv.x += t;

    // Color del arcoíris que cambia con el tiempo
    vec3 rainbowColor = vec3(sin(iTime), sin(iTime + 2.0), sin(iTime + 4.0));

    // Dibujar las letras con el color del arcoíris
    if ( // 'J'
        (uv.x > 0.4 && uv.x < 0.42 && uv.y > 0.4 && uv.y < 0.6) ||
        (uv.y > 0.38 && uv.y < 0.4 && uv.x > 0.3 && uv.x < 0.42) ||
        (uv.y > 0.6 && uv.y < 0.62 && uv.x > 0.3 && uv.x < 0.42) ||
        // 'U'
        (uv.x > 0.44 && uv.x < 0.46 && uv.y > 0.4 && uv.y < 0.6) ||
        (uv.y > 0.38 && uv.y < 0.4 && uv.x > 0.44 && uv.x < 0.56) ||
        (uv.x > 0.54 && uv.x < 0.56 && uv.y > 0.4 && uv.y < 0.6) ||
        // 'L'
        (uv.x > 0.6 && uv.x < 0.62 && uv.y > 0.4 && uv.y < 0.6) ||
        (uv.y > 0.38 && uv.y < 0.4 && uv.x > 0.6 && uv.x < 0.72) ||
        // 'I'
        (uv.x > 0.74 && uv.x < 0.76 && uv.y > 0.4 && uv.y < 0.6) ||
        // 'O'
        (length(uv - vec2(0.82, 0.5)) < 0.1 && length(uv - vec2(0.82, 0.5)) > 0.08)
    )
    {
        col = rainbowColor;
    }

    fragColor = vec4(col, 1.0);
}
