float blockSize=6.0;
vec3 monoColor=vec3(0.67,0.97,0.07);

vec2 get_pixelated_uv(vec2 uv)
{
    vec2 finalUV=uv;
    float size=blockSize+floor((5.0f*sin(2.0f*iTime)));
    finalUV.x=(floor(finalUV.x/size))*size;
    finalUV.y=(floor(finalUV.y/size))*size;
    return finalUV;
}

vec3 get_scaled_color(vec3 color)
{
    vec3 finalColor=color;
    float avg=(finalColor.r+finalColor.g+finalColor.b)/3.0;
    finalColor=mix(vec3(0.0),monoColor,avg);
    return finalColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    uv=get_pixelated_uv(uv)/iResolution.xy;
    //uv=uv/iResolution.xy;
    vec3 col=vec3(texture(iChannel0,uv));
    col=get_scaled_color(col);
    // Output to screen
    fragColor = vec4(col,1.0);
}
