/// by Dom Mandy in 2023


void mainImage(out vec4 o, vec2 i)
{
    // Edge distortion
    vec4 c = .01 * sqrt(texture(iChannel1, i / iResolution.xy));

    // Gamma correction
    vec4 s = sqrt(texture(iChannel0, c.xy + i / iResolution.xy));

    // Luma * 8
    float v = dot(s, vec4(2, 7, .7, 0));

    // Edges
    vec4 f = fwidth(s);

    o = min(1., log(.07 / max(max(f.x, f.y), f.z))) 
        * vec4(
            vec4(0, 4, 2, 6)[int(i.x) % 4] < v && 
            vec4(1, 5, 3, 7)[int(i.y) % 4] < v
        );    
}
