#define B(c,v)  O = mix( O, vec4(c), smoothstep(12./R.y,0., v ) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 4.5*( u+u - R ) / R.y +.5,
         I = ceil(U), A = abs(U-.5);
    O = vec4(.5 + .3*mod(I.x+I.y,2.));

    if (max(A.x,A.y) < 4. )
        I = ceil( U = abs(U-.5) ),
        A = abs( U = fract(U)-.5 ),
        B( mod(I.x+I.y+ceil(iTime),2.), length(U)-.25 ),
        B( .65, min(A.x,A.y) );        
}
