void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wind = 0.01 *((sin(iTime + uv.x)) / (1.1 - uv.x));

    float unRed = step(0.33 + wind, uv.y) * (1.0 - step(0.66 + wind, uv.y)); 

    // Output to screen
    fragColor = vec4(1.0, 1.0 - unRed, 1.0 -unRed, 1.0);
}
