#define PI 3.14159265359

vec2 bend( vec2 uv, float angle )
{
    const float BLOCK_HEIGHT = 1.;
    const float BLOCK_WIDTH = .25;
    float pi2 = PI / 2.;

    float bendSign = angle < 0. ? -1. : 1.;
    float bendRadius = BLOCK_HEIGHT / abs( angle );

    float df =  BLOCK_HEIGHT / (2.*PI);


    float moveToRight = mod((BLOCK_WIDTH + df*.5)*((floor((iTime*3.)/PI)  )), 3.7); 


    vec2 p = uv * vec2( bendSign, 1. ) + vec2( bendRadius, 0. );
    p.x -=  bendSign*moveToRight;

    return vec2( ( length( p ) - bendRadius ) / BLOCK_WIDTH * bendSign + .5, atan( p.y, p.x ) / abs( angle ) + .5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    uv.x += 1.5;

    float bendAngle = 2.*PI*sin(PI/2. + (mod(iTime*3., PI))); //[1, -1] *2PI => [-2pi, 2pi]
    float j = (3./2.)*PI;
    float sign1 = float(int(mod(iTime*3. / PI, j))% 2) == 1. ? 1. : -1.;

    vec2 textureUV = bend( uv, bendAngle );
    float mask = textureUV.x >= 0. && textureUV.x < 1. && textureUV.y >= 0.5 && textureUV.y < 1. ? 1. : 0.;

   vec2 frac1 = fract(textureUV*vec2(sign1, 1.));

   // Pattern for snake
   vec2 frac2 = fract(textureUV*4.);
   float abstand = 0.15;
   float pattern = step(abs(abs(frac2.x-0.5) + (frac2.y-0.15) -0.5)-abstand,0.);

   vec3 col = (vec3(frac1.x, 0.1, .5)      + vec3(pattern)) * mask;
   col += float(uv.x > 1.) * vec3(frac1,0.) * 0.5 * (1.-mask); // Show the fract as background on right screen-half

   fragColor = vec4(pow(col,vec3(1./2.2)),1.0);
}

