// UI audio fill bar - https://www.twitch.tv/videos/1662202941

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy) / iResolution.xy; // uv0-1

    vec4 backcol = vec4(0.1,0.1,0.1,1.0);
    vec4 barcolempty = vec4(0.2,0.2,0.2,1.0);
    vec4 barcolfull = vec4(0.8,0.8,0.2,1.0);

    float nseg = 10.0;

    float fill = fract(iTime*0.3)*(nseg+1.0);
    //float fill = 5.;     // 0-10
    fill = clamp(fill-1.0,0.0,nseg);

    float sx = 0.4;
   	float sy = 0.4;

	uv.y -= 0.5;

    float c = (floor(uv.x*(nseg)));
	uv.x = mod(uv.x, 1.0/nseg)-(0.5/nseg);
	float r = length(max(abs(uv) - vec2(sx/nseg,sy),0.0));

    float b = 1.0/iResolution.y; // 0.005

	r = smoothstep(b,0.0,r);

    vec4 col = (fill<=c) ?  barcolempty : barcolfull;
	fragColor = mix(backcol,col,r);
}
