void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 mo = ((-iResolution.xy + 2.0*iMouse.xy)/iResolution.y);
vec2 pos = fragCoord/iResolution.xy;
//pos = (2.*fragCoord-iResolution.xy)/iResolution.x;

pos*=2.+mo.y;
pos.x*=-1.;

pos*=rota(iTime/10.*pi);
//pos*=rota(iTime/10.*pi);
//pos=(pos/dot(pos,pos))+mo;

pos=abs(pos+vec2(cos(iTime/10.*pi),sin(iTime/10.*pi)));
//pos=cpow(pos,mo.y+1.);
//pos/=dot(pos,pos);
//pos/=2.;
    float res = 256.0;

    float col = float(int(fract(pos.x)*res) ^ int(fract(pos.y)*res)) / res;
if  (true)//((pos.x<=0.&&pos.x>=1.&&pos.y<=0.&&pos.y>=1.)==false)
{fragColor=mix(texture(iChannel0,pos),vec4(col),0.25);}else
{
fragColor=vec4(vec3(col),1.);
}
}

#define pi acos(-1.)
vec2 cpow(vec2 c,float n){
float a  = atan(c.y,c.x)*n;
float l = pow(length(c),n);
return l*vec2(cos(a),sin(a));}
mat2 rota(float ro){
float c = cos(ro);
float s = sin(ro);
return mat2(c,-s,s,c);}
