//https://upload.wikimedia.org/wikipedia/commons/thumb/2/2c/2D_affine_transformation_matrix.svg/250px-2D_affine_transformation_matrix.svg.png
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_coo_nor = (fragCoord.xy-iResolution.xy*0.5) / iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy-iResolution.xy*0.5) / iResolution.y;
    o_pix_coo_nor*= 6.0;
    o_mou_coo_nor*= 6.0;
    float n_tau = 6.2831;
    float n_amp = 10.;
    float n_th = 1.0;

    vec2 o_linewidth = vec2(0.01, 0.02);
    vec2 o_circle_width = vec2(0.1, 0.12);

    //o_pix_coo_nor.x = sin(n_tau+o_pix_coo_nor.x)*n_amp*o_mou_coo_nor.x+n_amp*o_mou_coo_nor.x;
    //o_pix_coo_nor.y = cos(n_tau+o_pix_coo_nor.y)*n_amp*o_mou_coo_nor.x+n_amp*o_mou_coo_nor.x;


    vec2 o_ihat = vec2(1.0, 0.0);
    vec2 o_jhat = vec2(0.0, 1.0);

    float n_dist_mouse_ihat = length(o_ihat-o_mou_coo_nor);
    float n_dist_mouse_jhat = length(o_jhat-o_mou_coo_nor);


    vec2 o_ihat_target = vec2(1.0, -2.0);
    vec2 o_ihat_target_delta = vec2(o_ihat_target.x-o_ihat.x, o_ihat_target.y-o_ihat.y);
    o_ihat = vec2(
       o_ihat.x + sin(iTime)*(o_ihat_target_delta.x/2.0)+(o_ihat_target_delta.x/2.0),
       o_ihat.y + sin(iTime)*(o_ihat_target_delta.y/2.0)+(o_ihat_target_delta.y/2.0)
    );
    vec2 o_jhat_target = vec2(3.0, 0.0);
    vec2 o_jhat_target_delta = vec2(o_jhat_target.x-o_jhat.x, o_jhat_target.y-o_jhat.y);
    o_jhat = vec2(
       o_jhat.x + sin(iTime)*(o_jhat_target_delta.x/2.0)+(o_jhat_target_delta.x/2.0),
       o_jhat.y + sin(iTime)*(o_jhat_target_delta.y/2.0)+(o_jhat_target_delta.y/2.0)
    );


    vec2 o_p = vec2(-1.0, 2.0);
    float n_dist_o_p = length(o_pix_coo_nor-o_p);

    float n_dist_origin = length(o_pix_coo_nor);
    float n_dist_ihat = length(o_ihat-o_pix_coo_nor);
    float n_dist_jhat = length(o_jhat-o_pix_coo_nor);

    vec4 o_col_text = texture(iChannel0, o_pix_coo_nor*-1.);



    fragColor = o_col_text;
    vec2 o_dist_nor = vec2(
        mod(o_pix_coo_nor.x, n_th),
        mod(o_pix_coo_nor.y, n_th)
    )/n_th;
    fragColor += smoothstep(o_linewidth.y,o_linewidth.x,o_dist_nor.x);
    fragColor += smoothstep(o_linewidth.y,o_linewidth.x,o_dist_nor.y);



    fragColor = mix(fragColor, vec4(1.0,0.0,0.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_origin));
    fragColor = mix(fragColor, vec4(0.0,1.0,0.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_ihat));
    fragColor = mix(fragColor, vec4(0.0,0.0,1.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_jhat));
    fragColor = mix(fragColor, vec4(0.0,1.0,1.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_o_p));

    //fragColor += vec4(0.0,0.0,smoothstep(0.1,0.12,n_dist_jhat), 1.0);




    o_pix_coo_nor = vec2(
        o_ihat.x * o_pix_coo_nor.x + o_jhat.x * o_pix_coo_nor.y,
        o_ihat.y * o_pix_coo_nor.x + o_jhat.y * o_pix_coo_nor.y
    );
    o_col_text = texture(iChannel0, o_pix_coo_nor*-1.);

    fragColor = mix(fragColor, o_col_text, 0.5);

    //fragColor += vec4(0.0,0.0,smoothstep(0.1,0.12,n_dist_jhat), 1.0);
    n_dist_o_p = length(o_pix_coo_nor-o_p);

    n_dist_origin = length(o_pix_coo_nor);
    n_dist_ihat = length(o_ihat-o_pix_coo_nor);
    n_dist_jhat = length(o_jhat-o_pix_coo_nor);



    o_dist_nor = vec2(
        mod(o_pix_coo_nor.x, n_th),
        mod(o_pix_coo_nor.y, n_th)
    )/n_th;
    fragColor += smoothstep(o_linewidth.y,o_linewidth.x,o_dist_nor.x);
    fragColor += smoothstep(o_linewidth.y,o_linewidth.x,o_dist_nor.y);

    n_dist_o_p = length(o_pix_coo_nor-o_p);


    fragColor = mix(fragColor, vec4(1.0,0.0,0.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_origin));
    fragColor = mix(fragColor, vec4(0.0,1.0,0.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_ihat));
    fragColor = mix(fragColor, vec4(0.0,0.0,1.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_jhat));
    fragColor = mix(fragColor, vec4(0.0,1.0,1.0,1.0), smoothstep(o_circle_width.y,o_circle_width.x,n_dist_o_p));

}
