// Fork of "Fork Grinning F sleeplessm 792" by sleeplessmonk. https://shadertoy.com/view/NldSDl
// 2022-11-18 08:50:10

//based on code from fragmentarium
//http://syntopia.github.io/Fragmentarium/

vec3 fractal(vec2 c, vec2 c2) {	
	vec2 z = c;
	float ci = 0.0;
	float mean = 0.0;
	for(int i = 0;i < 64; i++) {
		vec2 a = vec2(z.x,abs(z.y));
		float b = atan(a.y, a.x);
		if(b > 0.0) b -= 6.283185307179586;
		z = vec2(log(length(a)),b) + c2;
		if (i>5) mean+=length(z);
	}
	mean/=float(62);
	ci =  1.0 - log2(.05*log2(mean/1.));
	return vec3( .5+.5*cos(6.*ci+0.0),.4+.5*cos(66.*ci + 0.4),.5+.5*cos(6.*ci +1.7) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;

	vec2 uv = fragCoord.xy - iResolution.xy*.5;
	uv /= iResolution.x;
	vec2 tuv = uv;
	float rot=sin(iTime*0.025*mouse.y)*4.7;		
	uv.x = tuv.x*cos(rot)-tuv.y*sin(rot);
	uv.y = tuv.x*sin(rot)+tuv.y*cos(rot);
	float juliax = mouse.x * sin(iTime) * 0.021 + 0.12;
	float juliay = mouse.y * cos(iTime * 0.23) * 0.2 + 5.7;
	fragColor = vec4( fractal(uv, vec2(juliax, juliay)) ,1.0);
}
