void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(texture(iChannel0, uv/8.+vec2(0.,0.)).r,texture(iChannel0, uv/8.-vec2(0.5,0.5)).g,texture(iChannel0, uv/8.-vec2(7./8.,7./8.)).b);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
    if(uv.x < 1./8.) {
        if (uv.y < 1./8.) {
            uv = uv * 8.;
            fragColor = texture(iChannel0, uv);
        } else {
            fragColor = texture(iChannel1, uv - vec2(-8./8.,1./8.));
        }
    } else {
        fragColor = texture(iChannel1, uv - vec2(1./8.,0.));
    }
}
